import React,{Component} from 'react'
import axios from 'axios'
//import {getInitialData} from '../datprovider'

class Table extends Component {
    constructor() {
        super()
        this.state={
            showButton: 'true',
            phoneMap: []
        } 
        this.API_URL='http://localhost:5000'
        this.populateResponse = this.populateResponse.bind(this)
        this.fetchData = this.fetchData.bind(this)
        this.updatePhoneMap = this.updatePhoneMap.bind(this)
        this.call=this.call.bind(this)

    }
    call = () => {
       // console.log('Inside call')
        axios.post('http://localhost:5000/start')
            .then(resp => console.log('RESP->', resp))
        var eventSource = new EventSource(this.API_URL + '/getData');
        eventSource.addEventListener('update',  (e)=> {
            console.log(JSON.parse(e.data))
            this.updatePhoneMap(JSON.parse(e.data))
        },false)

    }
    updatePhoneMap = (data) => {
        console.log('data-', data)
        var origPhoneMap = [...this.state.phoneMap]
        for (var i = 0; i < origPhoneMap.length; i++)
                if (origPhoneMap[i].phoneNumber === data.phone) {
                 if(data.status!='')
                 origPhoneMap[i].status=data.status
             }  

    //     this.setState({phoneMap:origPhoneMap})
    }
    // updateData = (data) => {
    //     var origPhoneMap = [...this.state.phoneMap]
    //         for (var i = 0; i < origPhoneMap.length; i++)
    //             if (origPhoneMap[i].phoneNumber === data.phone) {
    //             if(data.status!='')
    //             origPhoneMap[i].status=data.status
    //         }  

    //     this.setState({phoneMap:origPhoneMap})
    // }  
    populateResponse = (data) => {
        var origPhoneMap = [...this.state.phoneMap]
            data.map(k => {
                origPhoneMap.push(k)
            })
            this.setState({phoneMap:origPhoneMap})
    }

    fetchData = () => {
        axios.get(this.API_URL + '/')
            .then(resp=> this.populateResponse(resp.data))

    }

    componentDidMount() {
       this.fetchData()

    }


    render() {
        return (
            <div>
            <table >
                <tbody>
                <tr>
        <th>Phone Number</th>
        <th>Status</th>
                    </tr>
                    {this.state.phoneMap.map(k => (
                         <tr key={k.id}>
                         <td>{k.phoneNumber}</td>
                        <td>{k.status}</td>

              </tr>
                    ))}


                </tbody>

                </table>
                {this.state.showButton? <button onClick={this.call}>Call </button> :null}

                </div>
        )
    }
}
export default Table
