import React,{Component} from 'react'
import axios from 'axios'
import {getInitialData} from '../datprovider'

class Table extends Component {
    constructor() {
        super()
        this.state={
            showButton: 'true',
            phoneMap: getInitialData()
        } 
        this.call = this.call.bind(this)
        this.updateData=this.updateData.bind(this)

    }
    call = () => {
        console.log('Inside call')
        axios.post('http://localhost:5000/start')
        .then(resp=>console.log('RESP->',resp))

    }

    updateData = (data) => {
        var origPhoneMap = [...this.state.phoneMap]

        for (var i = 0; i < origPhoneMap.length; i++)
            if (origPhoneMap[i].phoneNumber === data.phone) {
                origPhoneMap[i].status=data.status
            }
        }



    componentDidMount() {

        var eventSource = new EventSource("http://localhost:5000/getData");
       eventSource.onmessage = (e) => this.updateData(JSON.parse(e.data));

    }


    render() {
        return (
            <div>
            <table >
                <tbody>
                <tr>
        <th>Phone Number</th>
        <th>Status</th>
                    </tr>
                    {this.state.phoneMap.map(k => (
                         <tr key={k.id}>
                         <td>{k.phoneNumber}</td>
                        <td>{k.status}</td>

              </tr>
                    ))}


                </tbody>

                </table>
                {this.state.showButton? <button onClick={this.call}>Call </button> :null}

                </div>
        )
    }
}
export default Table
