import React,{Component} from 'react'
import axios from 'axios'

class Table extends Component {
    constructor() {
        super()
        this.state={
            showButton: 'true',
            phoneMap:[]
        }

        this.call = this.call.bind(this)
        this.fetchData=this.fetchData.bind(this)
    }
    call = () => {
        console.log('Inside call')
        axios.post('http://localhost:5000/star')

    }
    fetchData = () => {
       // console.log('Inside fetchData')
        axios.get('http://localhost:5000/getdata')
        .then(resp=>this.updateData(resp.data))

    }
    updateData = (arr) => {
        var origPhoneMap = [ ...this.state.phoneMap ]
        arr.map(k => (origPhoneMap.push(k)))
       // console.log(origPhoneMap)
        this.setState({phoneMap:origPhoneMap})
    }
    componentDidMount() {
       // console.log('Inside component')
        this.fetchData()
    }


    render() {
        return (
            <div>
            <table >
                <tbody>
                <tr>
        <th>Phone Number</th>
        <th>Status</th>
                    </tr>
                    {this.state.phoneMap.map(k => (
                         <tr key={k.id}>
                         <td>{k.phoneNumber}</td>
                        <td>{k.status}</td>

              </tr>
                    ))}


                </tbody>

                </table>
                {this.state.showButton? <button onClick={this.call}>Call </button> :null}

                </div>
        )
    }
}
export default Table
